import React, { useState } from "react";
import { getAIMessage } from "../api/api";
import "./ChatWindow.css";

const initialOptions = [
  "Check Order Status",
  "Return an Order",
  "Cancel an Order",
  "Search for a Part",
  "Find Model Number"
];

function ChatWindow() {
  const [messages, setMessages] = useState([
    {
      role: "assistant",
      content:
        "ðŸ¤– Hello! I'm PartSelect's AI assistant. Use the buttons below or ask me anything about your appliance."
    }
  ]);
  const [input, setInput] = useState("");
  const [buttonsVisible, setButtonsVisible] = useState(true);
  const [loading, setLoading] = useState(false);

  const handleSend = async (text) => {
    const userMessage = { role: "user", content: text };
    setMessages((prev) => [...prev, userMessage]);
    setInput("");
    setButtonsVisible(false);
    setLoading(true);

    const aiResponse = await getAIMessage(text);
    setMessages((prev) => [...prev, aiResponse]);
    setLoading(false);
  };

  const handleQuickAction = (actionText) => {
    handleSend(actionText);
  };

  return (
    <div className="chat-container">
      <div className="messages-container">
        {messages.map((msg, idx) => (
          <div key={idx} className={`${msg.role}-message`}>
            {msg.content}
          </div>
        ))}
        {loading && (
          <div className="assistant-message-container">
            <div className="assistant-speaking">
              ðŸ¤– Speaking...
              <div className="mouth">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
          </div>
        )}

      </div>

      {buttonsVisible && (
        <div className="quick-actions">
          {initialOptions.map((text, idx) => (
            <button key={idx} onClick={() => handleQuickAction(text)}>
              {text}
            </button>
          ))}
        </div>
      )}

      <div className="input-area">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && input.trim() && handleSend(input)}
        />
        <button onClick={() => handleSend(input)} disabled={!input.trim() || loading}>
          Send
        </button>
      </div>
    </div>
  );
}

export default ChatWindow;
