import React, { useState } from "react";
import { getAIMessage } from "../api/api";
import "./ChatWindow.css";

const initialOptions = [
  "Check Order Status",
  "Return an Order",
  "Cancel an Order",
  "Search for a Part",
  "Find Model Number"
];

function ChatWindow() {
  const [messages, setMessages] = useState([
    {
      role: "assistant",
      content:
        "Hello, I'm the customer service bot. See the buttons for some of the things I'm capable of, or type below and I'll try to help you."
    }
  ]);
  const [input, setInput] = useState("");
  const [buttonsVisible, setButtonsVisible] = useState(true);

  const handleSend = async (text) => {
    const userMessage = { role: "user", content: text };
    setMessages((prev) => [...prev, userMessage]);

    // Hide buttons after first interaction
    if (buttonsVisible) setButtonsVisible(false);

    const aiResponse = await getAIMessage(text);
    setMessages((prev) => [...prev, aiResponse]);
    setInput("");
  };

  const handleQuickAction = (actionText) => {
    handleSend(actionText);
  };

  return (
    <div className="chat-container">
      <div className="messages-container">
        {messages.map((msg, idx) => (
          <div key={idx} className={`${msg.role}-message`}>
            {msg.content}
          </div>
        ))}
      </div>

      {buttonsVisible && (
        <div className="quick-actions">
          {initialOptions.map((text, idx) => (
            <button key={idx} onClick={() => handleQuickAction(text)}>
              {text}
            </button>
          ))}
        </div>
      )}

      <div className="input-area">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && input.trim() && handleSend(input)}
        />
        <button onClick={() => handleSend(input)} disabled={!input.trim()}>
          Send
        </button>
      </div>
    </div>
  );
}

export default ChatWindow;
