import React, { useEffect, useState } from "react";
import { Button, Space, Input } from "antd";
import "./ChatWindow.css";

const QuickActions = ({ onSelect }) => {
  const options = [
    "Check Order Status",
    "Return an Order",
    "Cancel an Order",
    "Search for a Part",
    "Find Model Number"
  ];

  return (
    <Space wrap style={{ marginTop: 16 }}>
      {options.map((label) => (
        <Button
          key={label}
          shape="round"
          style={{ backgroundColor: "#5F9EA0", color: "white", border: "none" }}
          onClick={() => onSelect(label)}
        >
          {label}
        </Button>
      ))}
    </Space>
  );
};

const ChatWindow = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  useEffect(() => {
    setMessages([
      {
        sender: "assistant",
        text: "Hello, I'm the customer service bot. See the buttons for some of the things I'm capable of, or type below and I'll try to help you."
      }
    ]);
  }, []);

  const handleSend = () => {
    if (!input.trim()) return;
    setMessages((prev) => [...prev, { sender: "user", text: input }]);
    setInput("");
    // 여기에 API 연동 또는 응답 처리 로직 추가 가능
  };

  const handleQuickAction = (action) => {
    setMessages((prev) => [...prev, { sender: "user", text: action }]);
    // action 에 따라 응답 자동 추가 등 추가 가능
  };

  return (
    <div className="messages-container">
      {messages.map((msg, idx) => (
        <div
          key={idx}
          className={
            msg.sender === "user" ? "user-message-container" : "assistant-message-container"
          }
        >
          <div className={msg.sender === "user" ? "user-message message" : "assistant-message message"}>
            {msg.text}
          </div>
        </div>
      ))}

      <QuickActions onSelect={handleQuickAction} />

      <div className="input-area">
        <Input.TextArea
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onPressEnter={(e) => {
            if (!e.shiftKey) {
              e.preventDefault();
              handleSend();
            }
          }}
          placeholder="Type your message..."
          autoSize={{ minRows: 2, maxRows: 4 }}
        />
        <Button onClick={handleSend} type="primary">
          Send
        </Button>
      </div>
    </div>
  );
};

export default ChatWindow;
